import Header from "../shared/Header/Header";
import { useDispatch, useSelector } from "react-redux";
import {
  CardWrapper,
  Line,
  Image,
  TweetsTitle,
  FollowersTitle,
  Button,
  Avatar,
} from '../components/Card/Card.styled';
import img from '../images/img.svg';
import { follow, unfollow } from "../redux/slice";
export const Tweets = () => {

  const dispatch = useDispatch()
  const users = useSelector((state) => state.toolkit.users);
  const isFollowed = useSelector(state => state.toolkit.isFollowed)
  const handleFollow = () => {
    if (!isFollowed) {
      dispatch(follow());
    }
    };
    const handleUnfollow = () => {
    if (isFollowed) {
      dispatch(unfollow());
    }
  };
    return (
        <>
         <Header />
            {users.map(({user, avatar, tweets, followers, id}) => (
                <li key={id}>
                    <p>{user}</p>
                   <CardWrapper>
      <Image src={img} alt="quiz" width="308" height="168" />
      <Line />
       <img src={avatar} alt="avatar" width="64" height="64"/>
      <TweetsTitle>{tweets} tweets</TweetsTitle>
      <FollowersTitle>{followers.toLocaleString()} Followers</FollowersTitle>
      <Button type="button" onClick={!isFollowed ? handleFollow : handleUnfollow} isFollowed={isFollowed}>
        {isFollowed ? "Following" : "Follow"}
      </Button>
    </CardWrapper>
                </li>
            ))}
        </>
    )
}
